# install-apps.ps1
# Instalação automática de programas via Winget

$apps = @(
    "Bitwarden.Bitwarden",
    "OBSProject.OBSStudio",
    "Figma.Figma",
    "OpenJS.NodeJS.LTS",
    "Docker.DockerDesktop",
    "tailscale.tailscale"
)

foreach ($app in $apps) {
    Write-Host "Instalando $app..." -ForegroundColor Cyan
    winget install --accept-package-agreements --accept-source-agreements -e --id $app
    if ($LASTEXITCODE -eq 0) {
        Write-Host "$app instalado com sucesso!" -ForegroundColor Green
    } else {
        Write-Host "Falha ao instalar $app (código: $LASTEXITCODE)" -ForegroundColor Red
    }
}

Write-Host "Instalações concluídas." -ForegroundColor Yellow
Pause

